import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import htmlToImage from "html-to-image";
import personas from "./personas";
import "./Travel.css";

export default function TravelResult({ answers, onClose }) {
  const cardRef = useRef(null);
  const [playing, setPlaying] = useState(false);
  const audioRef = useRef(null);

  // Persona selection
  const base = personas[answers.style] || {
    name: "Traveler",
    animal: "üåç",
    img: "default.jpg",
    sound: null,
  };

  const toggleSound = () => {
    if (!base.sound) return;
    if (!audioRef.current) {
      audioRef.current = new Audio(`/${base.sound}`);
      audioRef.current.loop = true;
    }
    if (playing) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setPlaying(!playing);
  };

  const downloadCard = () => {
    if (cardRef.current) {
      // Add watermark before exporting
      const watermark = document.createElement("div");
      watermark.innerText = "¬© hasini de silva";
      watermark.style.position = "absolute";
      watermark.style.bottom = "8px";
      watermark.style.right = "12px";
      watermark.style.fontSize = "12px";
      watermark.style.color = "rgba(0,0,0,0.6)";
      cardRef.current.appendChild(watermark);

      htmlToImage.toPng(cardRef.current).then((dataUrl) => {
        const link = document.createElement("a");
        link.download = "travel-card.png";
        link.href = dataUrl;
        link.click();
        watermark.remove();
      });
    }
  };

  return (
    <div className="result-overlay">
      <motion.div
        className="result-card"
        ref={cardRef}
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <button className="close-btn" onClick={onClose}>‚úñ</button>

        <h1 className="big-animal">
          {base.animal}
        </h1>
        <h2>{base.name}</h2>

        <div className="result-summary">
          <p><strong>Style:</strong> {answers.style}</p>
          <p><strong>Companion:</strong> {answers.companion}</p>
          <p><strong>Setting:</strong> {answers.setting}</p>
          <p><strong>Pace:</strong> {answers.pace}</p>
        </div>

        <div className="travel-image">
          <img src={`/${base.img}`} alt={base.name} />
        </div>

        <p className="tip">
          ‚ú® Pack your bag with your {base.animal} spirit guiding you!
        </p>

        <div className="actions">
          <button onClick={toggleSound}>
            {playing ? "‚è∏ Stop Sound" : "üîä Feel Travel"}
          </button>
          <button onClick={downloadCard}>‚¨á Download Card</button>
        </div>
      </motion.div>
    </div>
  );
}
