// src/components/NICCalculator.jsx
import React, { useEffect, useRef, useState } from "react";

import { buildInsights, chineseZodiac } from "../utils/insights";
import "./NICCalculator.css";

function getBirthdayFromNIC(nic) {
  let year, dayCode;
  if (!nic) return null;

  if (nic.length === 12) {
    year = parseInt(nic.substring(0, 4), 10);
    dayCode = parseInt(nic.substring(4, 7), 10);
  } else if (nic.length === 10) {
    year = 1900 + parseInt(nic.substring(0, 2), 10);
    if (year < 1920) year += 100;
    dayCode = parseInt(nic.substring(2, 5), 10);
  } else {
    return null;
  }

  let gender = "Male";
  if (dayCode > 500) {
    gender = "Female";
    dayCode -= 500;
  }

  const birthdayDate = new Date(year, 0, dayCode);
  birthdayDate.setDate(birthdayDate.getDate() - 1);

  if (isNaN(birthdayDate.getTime())) return null;

  const birthday = birthdayDate.toISOString().split("T")[0];
  const age = new Date().getFullYear() - year;

  return {
    birthday,
    year,
    gender,
    age,
    type: birthdayDate.toDateString(),
  };
}

export default function NICCalculator() {
  const [nic, setNic] = useState("");
  const [details, setDetails] = useState(null);
  const [error, setError] = useState("");
  const audioRef = useRef(null);

  const handleCalculate = () => {
    const info = getBirthdayFromNIC(nic);
    if (info) {
      setDetails(info);
      setError("");
      if (audioRef.current) {
        audioRef.current.play();
      }
    } else {
      setDetails(null);
      setError("‚ùå Oops! Please enter a valid NIC number.");
    }
  };

  const zodiac = details ? chineseZodiac(details.year) : null;
  const insights = details ? buildInsights(new Date(details.birthday)) : {};

  return (
    <div className="nic-card">
      <h1 className="title">NIC Birthday Insights üéâ</h1>

      {/* Input */}
      <div className="input-row">
        <input
          type="text"
          placeholder="Enter NIC number..."
          value={nic}
          onChange={(e) => setNic(e.target.value)}
        />
        <button onClick={handleCalculate}>Check</button>
      </div>

      {/* Cute error */}
      {error && <div className="error-box">{error}</div>}

      {/* Audio */}
      <audio ref={audioRef} src="/sounds/success.mp3" preload="auto" />

      {/* Birthday details */}
      {details && (
        <div className="birthday-details">
          <p><strong>Birthday:</strong> {details.birthday}</p>
          <p><strong>Age:</strong> {details.age}</p>
          <p><strong>Gender:</strong> {details.gender}</p>
          <p><strong>Birthday Type:</strong> {details.type}</p>
        </div>
      )}

      {/* Cards grid */}
      {details && (
        <div className="cards-grid">
          {/* Chinese Zodiac */}
          <div className="info-card zodiac-card">
            <h2>üê≤ Chinese Zodiac</h2>
            {zodiac && (
              <div className="zodiac-content">
                <img
                  src={`/zodiac/${zodiac.animal}.png`}
                  alt={zodiac.animal}
                  className="zodiac-img"
                />
                <h3>{zodiac.animal}</h3>
              </div>
            )}
          </div>

          {/* Share Birthday */}
          <div className="info-card">
            <h2>üéÇ Share Birthday</h2>
            <p>{insights.sharedBirthday}</p>
          </div>

          {/* On This Day */}
          <div className="info-card">
            <h2>üìÖ On This Day</h2>
            <p>{insights.onThisDay}</p>
          </div>

          {/* More Ways */}
          <div className="info-card">
            <h2>‚ú® More from Your Birth Year</h2>
            <p>{insights.moreWays}</p>
          </div>
        </div>
      )}
    </div>
  );
}
