// src/components/NICCalculator.jsx
import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { buildInsights, chineseZodiac } from "../utils/insights"; // buildInsights exists in your utils

// ---------------------- Utility functions (kept from your original) ----------------------
function getBirthdayFromNIC(nic) {
  let year, dayCode;
  if (!nic) return "❌ Invalid NIC format";
  if (nic.length === 12) {
    year = parseInt(nic.substring(0, 4), 10);
    dayCode = parseInt(nic.substring(4, 7), 10);
  } else if (nic.length === 10) {
    year = 1900 + parseInt(nic.substring(0, 2), 10);
    if (year < 1920) year += 100;
    dayCode = parseInt(nic.substring(2, 5), 10);
  } else {
    return "❌ Invalid NIC format";
  }
  let gender = "Male";
  if (dayCode > 500) {
    gender = "Female";
    dayCode -= 500;
  }
  const birthdayDate = new Date(year, 0, dayCode);
  birthdayDate.setDate(birthdayDate.getDate() - 1);
  const month = birthdayDate.getMonth() + 1;
  const day = birthdayDate.getDate();
  const birthday = `${year}-${String(month).padStart(2, "0")}-${String(
    day
  ).padStart(2, "0")}`;
  return { birthday, gender };
}

function getDayOfWeek(dateStr) {
  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  return days[new Date(dateStr).getDay()];
}
function calculateAge(dateStr) {
  const today = new Date();
  const d = new Date(dateStr);
  let age = today.getFullYear() - d.getFullYear();
  const m = today.getMonth() - d.getMonth();
  if (m < 0 || (m === 0 && today.getDate() < d.getDate())) age--;
  return age;
}
function isBirthdayToday(dateStr) {
  const t = new Date();
  const d = new Date(dateStr);
  return t.getDate() === d.getDate() && t.getMonth() === d.getMonth();
}

// ---------------------- NICCalculator component ----------------------
export default function NICCalculator() {
  const navigate = useNavigate();

  // NIC + results
  const [nic, setNic] = useState("");
  const [result, setResult] = useState(null); // { birthday, gender, dayName, age, birthdayToday }
  const [preview, setPreview] = useState(null); // live preview object or error
  const [insights, setInsights] = useState(null); // payload from buildInsights
  const [loadingInsights, setLoadingInsights] = useState(false);
  const [error, setError] = useState(null);

  // refs for confetti canvas and to track animation loop
  const canvasRef = useRef(null);
  const confettiRAFRef = useRef(null);

  // -- update background & floating character on mount
  useEffect(() => {
    document.body.classList.add("default");
    const char = document.getElementById("floatingCharacter");
    if (char) char.style.backgroundImage = "url('/assets/couple.jpg')";

    // cleanup: keep default class but remove others
    return () => {
      document.body.classList.remove("male", "female");
    };
  }, []);

  // Live preview: whenever nic changes attempt to parse and set preview (no side-effects)
  useEffect(() => {
    if (!nic || nic.trim().length === 0) {
      setPreview(null);
      setError(null);
      return;
    }
    const parsed = getBirthdayFromNIC(nic.trim());
    if (typeof parsed === "string") {
      setPreview(null);
      setError(parsed);
    } else {
      const dayName = getDayOfWeek(parsed.birthday);
      const age = calculateAge(parsed.birthday);
      const birthdayToday = isBirthdayToday(parsed.birthday);
      setPreview({ ...parsed, dayName, age, birthdayToday });
      setError(null);
    }
  }, [nic]);

  // ---------------- confetti (pure react canvas drawing) ----------------
  const launchConfetti = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const confetti = [];
    for (let i = 0; i < 200; i++) {
      confetti.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height - canvas.height,
        r: Math.random() * 6 + 2,
        d: Math.random() * 200,
        color: `hsl(${Math.random() * 360}, 90%, 60%)`,
      });
    }

    let angle = 0;

    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      confetti.forEach((c) => {
        ctx.beginPath();
        ctx.arc(c.x, c.y, c.r, 0, Math.PI * 2);
        ctx.fillStyle = c.color;
        ctx.fill();
        ctx.strokeStyle = "rgba(255,255,255,0.6)";
        ctx.stroke();
        c.y += Math.cos(angle + c.d) + 1 + c.r / 2;
        c.x += Math.sin(angle);
        if (c.y > canvas.height) {
          c.y = -10;
          c.x = Math.random() * canvas.width;
        }
      });
      angle += 0.01;
      confettiRAFRef.current = requestAnimationFrame(draw);
    }

    draw();

    // stop after 4s
    setTimeout(() => {
      if (confettiRAFRef.current) cancelAnimationFrame(confettiRAFRef.current);
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }, 4000);
  };

  // ---------------- update body gradient & character image based on gender (react way) ----------------
  const applyGenderStyle = (gender) => {
    document.body.classList.remove("default", "male", "female");
    const char = document.getElementById("floatingCharacter");
    if (gender === "Female") {
      document.body.classList.add("female");
      if (char) char.style.backgroundImage = "url('/assets/girl.jpg')";
    } else {
      document.body.classList.add("male");
      if (char) char.style.backgroundImage = "url('/assets/boy.jpg')";
    }
  };

  // ---------------- onCalculate: full flow when user clicks Reveal Birthday ----------------
  const onCalculate = async () => {
    setLoadingInsights(true);
    setInsights(null);
    setError(null);

    const parsed = getBirthdayFromNIC(nic.trim());
    if (typeof parsed === "string") {
      setResult({ error: parsed });
      setLoadingInsights(false);
      return;
    }

    const dayName = getDayOfWeek(parsed.birthday);
    const age = calculateAge(parsed.birthday);
    const birthdayToday = isBirthdayToday(parsed.birthday);

    const computed = { ...parsed, dayName, age, birthdayToday };
    setResult(computed);

    // visual + audio features
    launchConfetti();
    applyGenderStyle(parsed.gender);

    // speak
    const voiceMessage = birthdayToday
      ? `Hey! It's your birthday today! so wish your happy birthday dear!! You are ${parsed.gender}.`
      : `Your birthday is ${parsed.birthday}. You are ${parsed.gender}.`;
    try {
      const utterance = new SpeechSynthesisUtterance(voiceMessage);
      utterance.lang = "en-US";
      utterance.rate = 1;
      utterance.pitch = 1;
      window.speechSynthesis.speak(utterance);
    } catch (e) {
      // ignore if speech fails
      console.warn("speech failed", e);
    }

    // Build insights via your existing module (pure fetch inside buildInsights)
    try {
      const dob = new Date(parsed.birthday);
      const payload = await buildInsights(dob); // uses your existing utils
      setInsights(payload);
    } catch (e) {
      console.error("Insights failed", e);
      setError("Insights failed to load.");
    } finally {
      setLoadingInsights(false);
    }
  };

  // ---------------- Travel navigation handler (pass state to TravelScenario) ----------------
  const handleGoTravel = () => {
    // Persona simple logic: can later be replaced by quiz result
    let persona = "Adventure Romantic";
    if (result?.age > 30) persona = "Culture Seeker";
    if (result?.age < 18) persona = "Young Explorer";

    const payload = {
      birthday: result?.birthday,
      age: result?.age,
      gender: result?.gender,
      persona,
    };

    navigate("/travel", { state: payload });
  };

  // ---------------- cleanup on unmount ----------------
  useEffect(() => {
    return () => {
      if (confettiRAFRef.current) cancelAnimationFrame(confettiRAFRef.current);
    };
  }, []);

  // simple helper to show zodiac (we imported chineseZodiac from utils)
  const zodiacStr = result?.birthday ? chineseZodiac(new Date(result.birthday).getFullYear()) : null;

  // ---------------- JSX ----------------
  return (
    <section className="card">
      <div className="input-row">
        <input
          type="text"
          placeholder="Enter NIC Number"
          value={nic}
          onChange={(e) => setNic(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") onCalculate();
          }}
          aria-label="NIC number"
        />
        <button onClick={onCalculate} aria-label="Reveal Birthday">
          Reveal Birthday
        </button>
      </div>

      {/* Live preview (shows instantly as user types a valid NIC) */}
      <div className="result" style={{ opacity: preview ? 1 : 0.8, transition: "opacity 0.3s" }}>
        {error && <div className="error">{error}</div>}

        {preview && !preview.error && (
          <div className="grid" aria-live="polite">
            <div>🎉 Birthday: <b>{preview.birthday}</b> <span className="badge">{preview.dayName}</span></div>
            <div>👤 Gender: <b>{preview.gender}</b></div>
            <div>🎂 Age: <b>{preview.age}</b></div>
            {preview.birthdayToday && <div className="happy">🎈 Happy Birthday! 🎈</div>}
          </div>
        )}
      </div>

      {/* Confetti canvas (pure React canvas) */}
      <canvas id="confettiCanvas" ref={canvasRef} style={{ position: "fixed", top: 0, left: 0, pointerEvents: "none", zIndex: 0 }} />

      {/* Insights section (rendered in React, no DOM-inserted HTML or vanilla event listeners) */}
      <section id="insights-container" style={{ marginTop: 18 }}>
        <h2>Fun Facts & Insights 🎉</h2>

        {loadingInsights && <p>Loading insights…</p>}

        {insights && (
          <>
            <div style={{ display: "flex", justifyContent: "flex-end" }}>
              <button className="travel-btn" onClick={handleGoTravel}>View Travel Card</button>
            </div>

            <div className="cards" style={{ marginTop: 12 }}>
              <div className="card">
                <h3>Chinese Zodiac</h3>
                <p>{zodiacStr}</p>
              </div>

              <div className="card">
                <h3>Shared Birthdays</h3>
                {insights.people && insights.people.length ? (
                  insights.people.map((p, i) => (
                    <div key={i} className="line">
                      <a href={p.url} target="_blank" rel="noreferrer">{p.name}</a> — {p.bio || ""}
                    </div>
                  ))
                ) : (
                  <p className="empty">😔 No shared birthdays found.</p>
                )}
              </div>

              <div className="card">
                <h3>On This Day</h3>
                {insights.event ? (
                  <div>
                    <p><strong>{insights.event.year}</strong> — {insights.event.text}</p>
                    {insights.event.pages?.map((pg, idx) => (
                      <div key={idx}><a href={pg.url} target="_blank" rel="noreferrer">{pg.title}</a></div>
                    ))}
                  </div>
                ) : (
                  <p className="empty">No historical event found.</p>
                )}
              </div>

              <div className="card">
                <h3>Movies from your birth year</h3>
                <div className="posters">
                  {insights.movies && insights.movies.length ? (
                    insights.movies.filter(m => m.poster).map((m, i) => (
                      <figure key={i}>
                        <img alt={m.title} src={m.poster} style={{ width: 80, borderRadius: 8 }} />
                        <figcaption>{m.title}</figcaption>
                      </figure>
                    ))
                  ) : (
                    <p className="empty">No movies available.</p>
                  )}
                </div>
              </div>
            </div>
          </>
        )}

        {!insights && !loadingInsights && <p className="muted">Run "Reveal Birthday" to load fun facts & extras.</p>}
      </section>
    </section>
  );
}
