// src/components/travel/TravelQuiz.jsx
import React, { useState } from "react";

/*
  TravelQuiz
  - Questions are lightweight and map options to persona points.
  - onPersonaSelect => live persona preview (optional)
  - onFinish => final persona when user clicks "Get Match"
*/

const PERSONAS = {
  zen: { id: "Zen Explorer", desc: "Calm, wellness-focused escapes (yoga, beaches, slow travel)" },
  party: { id: "Party Traveler", desc: "Nightlife & social hotspots (clubs, festivals)" },
  adventure: { id: "Adventure Romantic", desc: "Active trips: hiking, water-sports, micro-adventures" },
  culture: { id: "Culture Seeker", desc: "Museums, history, local cuisine & authentic stays" },
  relax: { id: "Relaxed Traveler", desc: "Comfort, cruises, slow-scene resorts" },
};

const QUESTIONS = [
  {
    id: "q1",
    q: "How do you like to spend a long weekend?",
    opts: [
      { t: "Beach & yoga", p: { zen: 2, relax: 1 } },
      { t: "City exploring + cafés", p: { culture: 2 } },
      { t: "Hiking & small adventures", p: { adventure: 2 } },
      { t: "Clubbing & parties", p: { party: 2 } },
      { t: "Spa + chill resort", p: { relax: 2 } },
    ],
  },
  {
    id: "q2",
    q: "Pick a favorite meal:",
    opts: [
      { t: "Sushi / seafood", p: { zen: 1, culture: 1 } },
      { t: "Street food & markets", p: { culture: 2 } },
      { t: "Campfire / grilled", p: { adventure: 1 } },
      { t: "Tasting menus", p: { culture: 1, relax: 1 } },
      { t: "Bar snacks & cocktails", p: { party: 2 } },
    ],
  },
  {
    id: "q3",
    q: "Who would you prefer to travel with?",
    opts: [
      { t: "Solo", p: { zen: 1, culture: 1 } },
      { t: "Partner (romantic)", p: { adventure: 1, relax: 1 } },
      { t: "Friends", p: { party: 2, adventure: 1 } },
      { t: "Family", p: { relax: 2, culture: 1 } },
      { t: "Group tour", p: { culture: 1, relax: 1 } },
    ],
  },
  {
    id: "q4",
    q: "What's your ideal pace?",
    opts: [
      { t: "Slow & mindful", p: { zen: 2, relax: 1 } },
      { t: "Packed & active", p: { adventure: 2, culture: 1 } },
      { t: "Festival & spontaneous", p: { party: 2 } },
      { t: "Cultural, museum-heavy", p: { culture: 2 } },
      { t: "Balanced mix", p: { relax: 1, culture: 1 } },
    ],
  },
  {
    id: "q5",
    q: "Choose an activity:",
    opts: [
      { t: "Yoga & sunrise", p: { zen: 2 } },
      { t: "Night market hopping", p: { culture: 1, party: 1 } },
      { t: "Trekking / canyoning", p: { adventure: 2 } },
      { t: "Winery / food tours", p: { culture: 2 } },
      { t: "Beach + poolside", p: { relax: 2 } },
    ],
  },
];

export default function TravelQuiz({ initialPersona = null, onPersonaSelect = () => {}, onFinish = () => {} }) {
  const [answers, setAnswers] = useState({});
  const [selectedPersona, setSelectedPersona] = useState(initialPersona);

  const selectOption = (qId, idx) => {
    setAnswers((s) => ({ ...s, [qId]: idx }));
    // live compute persona and call onPersonaSelect
    const live = scoreFromAnswers({ ...answers, [qId]: idx });
    setSelectedPersona(live?.persona || null);
    onPersonaSelect(live?.persona || null);
  };

  function scoreFromAnswers(ansObj) {
    const scores = { zen: 0, party: 0, adventure: 0, culture: 0, relax: 0 };
    for (const q of QUESTIONS) {
      const idx = ansObj[q.id];
      if (typeof idx === "number" && q.opts[idx]) {
        const pts = q.opts[idx].p || {};
        Object.keys(pts).forEach((k) => (scores[k] += pts[k]));
      }
    }
    // pick winner
    const winnerKey = Object.keys(scores).reduce((a, b) => (scores[a] >= scores[b] ? a : b));
    const personaId = PERSONAS[winnerKey].id;
    return { scores, persona: personaId, key: winnerKey };
  }

  const handleFinish = () => {
    const result = scoreFromAnswers(answers);
    setSelectedPersona(result.persona);
    onFinish(result.persona);
  };

  const resetQuiz = () => {
    setAnswers({});
    setSelectedPersona(null);
    onPersonaSelect(null);
    onFinish(null);
  };

  return (
    <div className="quiz-card">
      <div className="quiz-header">
        <h2>Travel Personality Quiz</h2>
        <p className="hint">Answer 5 quick questions — no sign-in. Live preview updates on the right.</p>
      </div>

      <div className="questions">
        {QUESTIONS.map((q, qi) => (
          <div className="question" key={q.id}>
            <div className="q-title">{qi + 1}. {q.q}</div>
            <div className="q-opts">
              {q.opts.map((o, oi) => {
                const checked = answers[q.id] === oi;
                return (
                  <label key={oi} className={`opt ${checked ? "checked" : ""}`}>
                    <input
                      type="radio"
                      name={q.id}
                      checked={checked}
                      onChange={() => selectOption(q.id, oi)}
                    />
                    <span>{o.t}</span>
                  </label>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      <div className="quiz-actions">
        <button className="btn-primary" onClick={handleFinish}>Get Match</button>
        <button className="btn-ghost" onClick={resetQuiz}>Reset</button>
        <div className="small-note">Tip: you can skip questions — the quiz still gives a best-fit result.</div>
      </div>

      <div className="live-persona">
        <small>Live persona:</small>
        <div className="persona-pill">{selectedPersona || "— not set yet —"}</div>
      </div>
    </div>
  );
}
