import React from "react";
import "./";

export default function TravelResult({ answers, onClose }) {
  // Persona mapping
  const personaMap = {
    "üå¥ Relaxation": { name: "Zen Explorer", animal: "üê¢", img: "beach.jpg" },
    "üó∫ Adventure": { name: "Wild Wanderer", animal: "ü¶Å", img: "mountain.jpg" },
    "üèõ Culture": { name: "Cultural Seeker", animal: "ü¶â", img: "city.jpg" },
    "üéâ Party": { name: "Nightlife Nomad", animal: "üê¨", img: "party.jpg" },
  };

  const base = personaMap[answers.style] || {
    name: "Traveler",
    animal: "üåç",
    img: "default.jpg",
  };

  return (
    <div className="result-overlay">
      <div className="result-card">
        <button className="close-btn" onClick={onClose}>
          ‚úñ
        </button>
        <h1>üåü Your Travel Persona üåü</h1>
        <h2>
          {base.animal} {base.name}
        </h2>

        <div className="result-summary">
          <p><strong>Style:</strong> {answers.style}</p>
          <p><strong>Companion:</strong> {answers.companion}</p>
          <p><strong>Setting:</strong> {answers.setting}</p>
          <p><strong>Pace:</strong> {answers.pace}</p>
        </div>

        <div className="travel-image">
          <img
            src={`/${base.img}`}
            alt={base.name}
          />
        </div>
        <p className="tip">
          ‚ú® Pro tip: Pack your bag with your {base.animal} spirit guiding you!
        </p>
      </div>
    </div>
  );
}
