// src/components/travel/TravelResult.jsx
import React, { useMemo } from "react";

/*
  TravelResult
  - Combines persona (from quiz) + birthMonth + age to produce a travel suggestion.
  - showCTA: shows Copy / Share button.
  - onFinal(payload) called when user clicks Copy so page can note finalization.
*/

const MONTH_THEMES = {
  1: "❄ Winter wonderland (Switzerland, Finland)",
  2: "💕 Romantic getaways (Paris, Venice)",
  3: "🌸 Nature awakening (Japan, Cherry Blossoms)",
  4: "⛰ Adventure hikes (Nepal, Peru)",
  5: "☀ Sunny escapes (Greece, Spain)",
  6: "🦁 Safari adventures (Kenya, Tanzania)",
  7: "🎉 Festival hotspots (Brazil, Spain)",
  8: "🏖 Beach vibes (Maldives, Bali)",
  9: "🏺 Cultural tours (Turkey, Egypt)",
  10: "🎃 Spooky/fall trips (Prague, Transylvania)",
  11: "🏜 Desert journeys (Dubai, Morocco)",
  12: "🎄 Christmas markets (Germany, Austria)",
};

function ageFlavor(age) {
  if (!age && age !== 0) return "Traveler of all ages";
  if (age < 18) return "🎢 Fun youth adventures (theme parks, camps)";
  if (age <= 29) return "🔥 Young adult adventures (Thailand, Ibiza)";
  if (age <= 49) return "🌿 Balanced culture & comfort (Italy, Spain)";
  return "🛳 Relaxing heritage journeys (cruises, historical cities)";
}

export default function TravelResult({ persona, birthMonth, age, gender, onFinal = () => {}, showCTA = false }) {
  const monthText = birthMonth ? MONTH_THEMES[birthMonth] : "🌍 Any-season escapes";

  // combine into final sentence
  const finalText = useMemo(() => {
    const p = persona || "Explorer";
    return `${p} • ${monthText} • ${ageFlavor(age)}${gender ? ` • ${gender}` : ""}`;
  }, [persona, monthText, age, gender]);

  const resultCardText = useMemo(() => {
    const title = persona ? `${persona} — Travel Match` : "Your Travel Match";
    const lines = [
      title,
      `Birth month vibe: ${monthText}`,
      `Age flavour: ${ageFlavor(age)}`,
      `Quick: ${persona || "Explorer"}${gender ? ` • ${gender}` : ""}`,
      "",
      "Find your tailored travel scenario — generated by the NIC Quiz app."
    ];
    return lines.join("\n");
  }, [persona, monthText, age, gender]);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(resultCardText);
      onFinal({ persona, month: birthMonth, age, gender, text: resultCardText });
      alert("Result copied — paste into LinkedIn or anywhere you like.");
    } catch (e) {
      alert("Copy failed. You can still manually copy the text below.");
    }
  };

  return (
    <div className="result-card">
      <div className="result-top">
        <div className="persona">{persona || "Explorer"}</div>
        <div className="badge-mini">{monthText}</div>
      </div>

      <div className="result-body">
        <p className="final-text">{finalText}</p>
        <ul className="detail-list">
          <li><strong>Birth month suggestion:</strong> {monthText}</li>
          <li><strong>Age flavour:</strong> {ageFlavor(age)}</li>
          {gender && <li><strong>Gender:</strong> {gender}</li>}
        </ul>
      </div>

      {showCTA && (
        <div className="result-actions">
          <button className="btn-primary" onClick={handleCopy}>Copy result (for LinkedIn)</button>
          <button className="btn-ghost" onClick={() => window.open("https://www.linkedin.com/", "_blank")}>Open LinkedIn</button>
        </div>
      )}
    </div>
  );
}
