// src/components/travel/TravelResult.jsx
import React, { useEffect, useRef, useState } from "react";
import personas from "./personas";
import MapExplorer from "./MapExplorer";
import * as htmlToImage from "html-to-image";
import "../TravelResult.css"; // styles below

// Build an Overpass tiny query for persona
function buildOverpassFiltersForPersona(personaKey) {
  // return array of element filters for Overpass (node/way)
  switch (personaKey) {
    case "üå¥ Relaxation":
      return [
        'node["natural"="beach"]',
        'way["natural"="beach"]',
        'node["tourism"="hotel"]',
        'node["tourism"="resort"]',
      ];
    case "üó∫ Adventure":
      return [
        'node["tourism"="attraction"]',
        'node["leisure"="trail"]',
        'way["highway"="path"]',
        'node["tourism"="viewpoint"]',
      ];
    case "üèõ Culture":
      return [
        'node["tourism"="museum"]',
        'node["historic"]',
        'node["amenity"="theatre"]',
      ];
    case "üéâ Party":
      return [
        'node["amenity"="nightclub"]',
        'node["amenity"="bar"]',
        'node["amenity"="restaurant"]',
      ];
    case "üçú Foodie":
      return [
        'node["amenity"="restaurant"]',
        'node["amenity"="cafe"]',
        'node["amenity"="fast_food"]',
      ];
    case "‚ùÑ Arctic":
      return [
        'node["natural"="peak"]',
        'node["tourism"="information"]',
        'node["natural"="glacier"]',
      ];
    default:
      return ['node["tourism"="attraction"]'];
  }
}

// fetch Overpass places around lat/lon (radius meters)
async function fetchPlacesOverpass(lat, lon, personaKey, radius = 30000, limit = 20) {
  const filters = buildOverpassFiltersForPersona(personaKey);
  // Construct Overpass QL
  const clauses = filters
    .map((f) => `${f}(around:${radius},${lat},${lon});`)
    .join("");
  const q = `[out:json][timeout:25];(${clauses});out center ${limit};`;
  const url = "https://overpass-api.de/api/interpreter?data=" + encodeURIComponent(q);
  const res = await fetch(url);
  if (!res.ok) throw new Error("Overpass failed: " + res.status);
  const data = await res.json();
  return data.elements || [];
}

export default function TravelResult({ answers = null, nicState = {}, onClose = () => {} }) {
  const [personaKey, setPersonaKey] = useState(answers?.style ?? null);
  const base = (personaKey && personas[personaKey]) ? personas[personaKey] : {
    name: "Traveler",
    animal: "üåç",
    img: "default.jpg",
    sound: null
  };

  const [coords, setCoords] = useState(null); // [lat, lon]
  const [places, setPlaces] = useState([]);
  const [loadingPlaces, setLoadingPlaces] = useState(false);
  const [errorPlaces, setErrorPlaces] = useState(null);
  const [playing, setPlaying] = useState(false);
  const audioRef = useRef(null);
  const cardRef = useRef(null);

  useEffect(() => {
    // personaKey from answers
    setPersonaKey(answers?.style ?? null);
  }, [answers]);

  useEffect(() => {
    // preload audio if exists
    if (base.sound) {
      audioRef.current = new Audio(base.sound);
      audioRef.current.loop = true;
    } else {
      audioRef.current = null;
    }
    return () => {
      if (audioRef.current) {
        try { audioRef.current.pause(); } catch {}
        audioRef.current = null;
      }
    };
  }, [personaKey]);

  const toggleSound = async () => {
    if (!audioRef.current) return;
    if (playing) {
      audioRef.current.pause();
      setPlaying(false);
    } else {
      try {
        await audioRef.current.play();
        setPlaying(true);
      } catch (e) {
        // autoplay may be blocked; user interaction required. handle gracefully.
        console.error("Audio play error", e);
        alert("Audio playback blocked by browser ‚Äî click the button again to enable sound.");
      }
    }
  };

  const requestGeolocationAndFind = () => {
    setErrorPlaces(null);
    setLoadingPlaces(true);
    if (!navigator.geolocation) {
      setErrorPlaces("Geolocation not supported. Showing fallback suggestions.");
      setLoadingPlaces(false);
      return;
    }
    navigator.geolocation.getCurrentPosition(
      async (pos) => {
        const lat = pos.coords.latitude;
        const lon = pos.coords.longitude;
        setCoords([lat, lon]);
        try {
          const el = await fetchPlacesOverpass(lat, lon, personaKey, 30000, 20);
          setPlaces(el);
        } catch (err) {
          console.error(err);
          setErrorPlaces("Places fetch failed. Try again later.");
        } finally {
          setLoadingPlaces(false);
        }
      },
      (err) => {
        console.warn("geolocation denied", err);
        setErrorPlaces("Geolocation denied. Use fallback suggestions.");
        setLoadingPlaces(false);
      },
      { timeout: 15000, maximumAge: 60 * 1000 }
    );
  };

  const downloadCard = async () => {
    if (!cardRef.current) return alert("Card element missing.");
    // append watermark in DOM (visible in exported image)
    const watermark = document.createElement("div");
    watermark.style.position = "absolute";
    watermark.style.right = "10px";
    watermark.style.bottom = "8px";
    watermark.style.color = "rgba(0,0,0,0.6)";
    watermark.style.fontSize = "12px";
    watermark.style.fontWeight = "600";
    watermark.innerText = "¬© hasini de silva";
    cardRef.current.appendChild(watermark);

    try {
      const dataUrl = await htmlToImage.toPng(cardRef.current, { cacheBust: true });
      const a = document.createElement("a");
      a.href = dataUrl;
      a.download = `${base.name.replace(/\s+/g, "_")}_travel_card.png`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    } catch (err) {
      console.error("export fail", err);
      alert("Export failed in this browser. You can still copy text manually.");
    } finally {
      watermark.remove();
    }
  };

  // derive some generated suggestions (frontend-only) ‚Äî more creative than echoing answers
  function craftPersonaStory() {
    // combine persona, setting and pace into a creative title + short plan
    const p = base.name;
    const settingEmoji = (answers?.setting || "").match(/[\u{1F300}-\u{1FAFF}]/u)?.[0] || "";
    const pace = answers?.pace || "";
    const palette = [
      `${p} ‚Äî ${settingEmoji} ${answers?.setting || "destination"}`,
      `Tagline: ${pace.includes("Slow") ? "Breathe, unwind & soak in quiet moments." : pace.includes("Fast") ? "Fast tracks, big thrills." : "A balanced wander with hidden gems."}`,
    ];
    return palette;
  }

  // fallback curated suggestions if geolocation / Overpass fails
  const fallbackSuggestions = (() => {
    switch (personaKey) {
      case "üå¥ Relaxation": return [{ name: "Maldives ‚Äî Atoll beaches", lat: 3.2028, lon: 73.2207, url: "https://www.google.com/maps/search/maldives+beach" }];
      case "üó∫ Adventure": return [{ name: "Chiang Mai treks, Thailand", lat: 18.7883, lon: 98.9853, url: "https://www.google.com/maps/search/chiang+mai+trekking" }];
      case "üèõ Culture": return [{ name: "Florence, Italy ‚Äî art & history", lat: 43.7696, lon: 11.2558, url: "https://www.google.com/maps/search/florence+italy+duomo" }];
      case "üéâ Party": return [{ name: "Ibiza, Spain ‚Äî nightlife", lat: 38.9067, lon: 1.4206, url: "https://www.google.com/maps/search/ibiza+party" }];
      case "üçú Foodie": return [{ name: "Bangkok street food, Thailand", lat: 13.7563, lon: 100.5018, url: "https://www.google.com/maps/search/bangkok+street+food" }];
      case "‚ùÑ Arctic": return [{ name: "Troms√∏, Norway ‚Äî northern lights", lat: 69.6492, lon: 18.9553, url: "https://www.google.com/maps/search/tromso+northern+lights" }];
      default: return [];
    }
  })();

  const personaStory = craftPersonaStory();

  return (
    <div className="result-overlay">
      <div className="result-card enhanced" ref={cardRef}>
        <button className="close-btn" onClick={() => { try { audioRef.current && audioRef.current.pause(); } catch {} onClose(); }}>‚úñ</button>

        <div className="result-top">
          <div className="animal-emoji-giant">{base.animal}</div>
          <div className="persona-title">
            <h2>{base.name}</h2>
            <p className="tagline">{personaStory[1]}</p>
          </div>
        </div>

        <div className="result-body">
          <div className="left">
            <div className="summary-card">
              <h3>Your vibe</h3>
              <p className="small">{personaStory[0]}</p>
              <div className="micro-itinerary">
                <strong>Micro-itinerary</strong>
                <ol>
                  <li>{answers?.style ? `Start with a ${answers.style.replace(/\p{Extended_Pictographic}/u,'').trim()}-style kickoff` : 'Kickoff day with local highlights'}</li>
                  <li>{answers?.setting ? `Explore ${answers.setting}` : 'Discover local favorites'}</li>
                  <li>{answers?.pace ? `${answers.pace} days with curated spots` : 'Relax and take it slow'}</li>
                </ol>
              </div>

              <div className="cta-row">
                <button className="btn-primary" onClick={requestGeolocationAndFind}>
                  {loadingPlaces ? "Finding places‚Ä¶" : "Find Places Nearby"}
                </button>

                <button className="btn-ghost" onClick={toggleSound}>
                  {playing ? "üîá Stop Sound" : "üîä Feel Travel"}
                </button>

                <button className="btn-download" onClick={downloadCard}>
                  Download Card (PNG)
                </button>
              </div>
            </div>
          </div>

          <div className="right">
            {/* Map view or fallback list */}
            {coords ? (
              <MapExplorer center={coords} places={places.length ? places : fallbackSuggestions} />
            ) : (
              <div className="places-list">
                <h4>Suggested places</h4>
                {(places.length ? places : fallbackSuggestions).slice(0,6).map((p, i) => (
                  <div key={i} className="place-line">
                    <div className="place-name">{p.tags?.name || p.display_name || p.name || p.name || fallbackSuggestions[i]?.name}</div>
                    <a className="open-google" href={p.url ?? `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(p.lat + "," + p.lon)}`} target="_blank" rel="noreferrer">Open in Maps</a>
                  </div>
                ))}
                {errorPlaces && <div className="error">{errorPlaces}</div>}
              </div>
            )}
          </div>
        </div>

        <div className="linkedin-caption">
          <strong>LinkedIn caption</strong>
          <textarea readOnly rows={3} value={`My travel persona: ${base.name} ${base.animal}\nA short plan: ${personaStory[1]}\n#TravelQuiz #NICDemo`} />
        </div>

        <div className="copyright">¬© hasini de silva</div>
      </div>
    </div>
  );
}
