import React, { useState, useRef } from "react";
import { motion } from "framer-motion";
import * as htmlToImage from "html-to-image";
import "./TravelResult.css";

export default function TravelResult({ answers, onClose }) {
  const cardRef = useRef(null);
  const [playSound, setPlaySound] = useState(false);

  // Persona mappings
  const personaMap = {
    "🌴 Relaxation": { name: "Zen Explorer", animal: "🐢", img: "beach.jpg", sound: "/sounds/beach.mp3" },
    "🗺 Adventure": { name: "Wild Wanderer", animal: "🦁", img: "mountain.jpg", sound: "/sounds/jungle.mp3" },
    "🏛 Culture": { name: "Cultural Seeker", animal: "🦉", img: "city.jpg", sound: "/sounds/city.mp3" },
    "🎉 Party": { name: "Nightlife Nomad", animal: "🐬", img: "party.jpg", sound: "/sounds/party.mp3" },
    "🌲 Nature": { name: "Eco Soul", animal: "🦌", img: "forest.jpg", sound: "/sounds/forest.mp3" },
    "🏖 Luxury": { name: "Elite Voyager", animal: "🦢", img: "luxury.jpg", sound: "/sounds/luxury.mp3" },
  };

  const base = personaMap[answers.style] || {
    name: "Traveler",
    animal: "🌍",
    img: "default.jpg",
    sound: "/sounds/default.mp3",
  };

  // Sound control
  const audioRef = useRef(null);
  const toggleSound = () => {
    if (!audioRef.current) {
      audioRef.current = new Audio(base.sound);
      audioRef.current.loop = true;
    }
    if (playSound) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setPlaySound(!playSound);
  };

  // Download PNG with watermark
  const handleDownload = async () => {
    if (!cardRef.current) return;
    const dataUrl = await htmlToImage.toPng(cardRef.current);
    const link = document.createElement("a");
    link.download = "travel-card.png";
    link.href = dataUrl;
    link.click();
  };

  return (
    <div className="result-overlay">
      <motion.div
        className="result-card"
        ref={cardRef}
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <button className="close-btn" onClick={onClose}>✖</button>
        <h1 className="title">🌟 Your Travel Persona 🌟</h1>
        <h2 className="persona">
          <span className="animal-icon">{base.animal}</span> {base.name}
        </h2>

        <div className="result-summary">
          <p><strong>Style:</strong> {answers.style}</p>
          <p><strong>Companion:</strong> {answers.companion}</p>
          <p><strong>Setting:</strong> {answers.setting}</p>
          <p><strong>Pace:</strong> {answers.pace}</p>
        </div>

        <div className="travel-image">
          <img src={`/${base.img}`} alt={base.name} />
        </div>

        <p className="tip">✨ Pack your bag with your {base.animal} spirit guiding you!</p>

        <div className="actions">
          <button onClick={toggleSound} className="sound-btn">
            {playSound ? "🔇 Stop Travel Feel" : "🔊 Feel Travel"}
          </button>
          <button onClick={handleDownload} className="download-btn">📥 Download</button>
        </div>

        <div className="linkedin-box">
          <h3>LinkedIn Caption Preview</h3>
          <p>
            Take a fun quiz to discover your unique Travel Persona 🌟  
            I got <b>{base.animal} {base.name}</b>! 🧳  
            #Travel #FunQuiz #ReactApp
          </p>
        </div>

        <footer className="watermark">© hasini de silva</footer>
      </motion.div>
    </div>
  );
}
