import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import * as htmlToImage from "html-to-image";
import personas from "./personas";
import "./TravelResult.css";

export default function TravelResult({ result, onClose }) {
  const [playing, setPlaying] = useState(false);
  const [captionCopied, setCaptionCopied] = useState(false);
  const audioRef = useRef(null);
  const cardRef = useRef(null);

  const persona = personas[result];
  if (!persona) return null;

  // Download PNG
  const handleDownload = async () => {
    if (cardRef.current) {
      const dataUrl = await htmlToImage.toPng(cardRef.current);
      const link = document.createElement("a");
      link.download = "travel-card.png";
      link.href = dataUrl;
      link.click();
    }
  };

  // Copy LinkedIn caption
  const handleCopyCaption = () => {
    const caption = `🌍 My Travel Persona: ${persona.name} ${persona.animal}
📍 Perfect style: ${persona.style}
✨ Suggested trip: ${persona.suggestions[0]} 

Created with ❤️ by Hasini De Silva’s Travel Persona App 🚀`;

    navigator.clipboard.writeText(caption).then(() => {
      setCaptionCopied(true);
      setTimeout(() => setCaptionCopied(false), 2000);
    });
  };

  // Toggle sound
  const toggleSound = () => {
    if (!audioRef.current) return;
    if (playing) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setPlaying(!playing);
  };

  return (
    <motion.div
      className="travel-overlay"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <motion.div
        ref={cardRef}
        className="travel-card"
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
      >
        {/* Close button */}
        <button className="close-btn" onClick={onClose}>
          ✖
        </button>

        {/* Header */}
        <h2 className="travel-title">
          🌟 Your Travel Persona 🌟
        </h2>
        <h3 className="persona-name">
          {persona.animal} {persona.name}
        </h3>

        {/* Image */}
        <img
          src={persona.img}
          alt={persona.name}
          className="persona-img"
        />

        {/* Fee Calculator */}
        <div className="estimate-box">
          <h4>💰 Estimate & Plan</h4>
          {/* Keep your calculation logic here */}
          <p><strong>Total:</strong> ${persona.basePrice * 5}</p>
          <p><em>(Calculated with companions, discounts & taxes)</em></p>
        </div>

        {/* Suggestions */}
        <div className="suggestion-box">
          <h4>✨ Creative Suggestions</h4>
          <ul>
            {persona.suggestions.map((s, i) => (
              <li key={i}>{s}</li>
            ))}
          </ul>
        </div>

        {/* Action buttons */}
        <div className="button-row">
          <button onClick={toggleSound}>
            {playing ? "🔇 Stop Sound" : "🔊 Feel Travel"}
          </button>
          <button onClick={handleDownload}>⬇ Download Card (PNG)</button>
        </div>

        {/* LinkedIn caption */}
        <div className="linkedin-box">
          <h4>📢 LinkedIn Caption</h4>
          <pre>{`🌍 My Travel Persona: ${persona.name} ${persona.animal}
📍 Perfect style: ${persona.style}
✨ Suggested trip: ${persona.suggestions[0]}

Created with ❤️ by Hasini De Silva 🚀`}</pre>
          <button onClick={handleCopyCaption}>
            {captionCopied ? "✅ Copied!" : "📋 Copy"}
          </button>
        </div>

        {/* Hidden audio */}
        <audio ref={audioRef} src={persona.sound}></audio>
      </motion.div>
    </motion.div>
  );
}
